import {useState} from "react";

function App() {

  let {number , setNumber} = useState(10);

  let handleClick = () =>{
    number++;
    setNumber(number);
  };


  return (
    <>
        <h1> {number} </h1>
        <button onClick = {handleClick} >Click </button>
    </>
  );
}

export default App;
